const Game = () => {
    const [songData, setSongData] = useState(null);
    const [reavealed, setReavealed] = useState(false);
    const [timeLeft, setTimeLeft] = useState(THINK_TIME);

    // setReavealed(false);

    useEffect(async () => {
        await set_volume(100);
        await Shuffle();
        Get_available_devices();
    }, []);

    useEffect(() => {
        const interval = setInterval(() => {
            setTimeLeft(prevTimeLeft => {
                if (prevTimeLeft === 0) {
                    clearInterval(interval);
                    return 0;
                }
                return prevTimeLeft - 1;
            });
        }, 1000);

        // Clean up the interval to avoid memory leaks
        return () => clearInterval(interval);
    }, [songData]); // Run only once on component mount
    return (
        <div>
            <button className="skip" onClick={async () => {
            const result = await NextQuestion();
            setSongData(result); 
            setReavealed(false);
            setTimeLeft(THINK_TIME)


            }}>Skip</button>
            {/* {console.log(songData)} */}

            <button className="skip" onClick={() => 
                setReavealed(true)
            }>Correct</button>

            <button className="skip" onClick={() => 
                setReavealed(false)
            }>Hide</button>

            <img className='track_image' src={songData && reavealed ? songData.cover : placeholder_playlist}></img>
            <div className="answer_track">Song: {songData && reavealed ? songData.name : ""}</div>
            <div className="timer">{`${Math.floor(timeLeft / 1000)} `}</div>
            <div className="answer_artists">Artist: {songData && reavealed ? GetArtists(songData.artists) : ""}</div>
            <div className="timer_header">Time Left:</div>
            <div className="timer">{`${timeLeft} `}</div>





        </div>
    )
}

