      <div>
        <img className='playlist_image' onClick={handle_playlist_click.bind(null, playlist_json.id)} src={playlist_json.images[0]?.url ?? placeholder_playlist}></img>
        <p className='playlist_name'>{playlist_json.name}</p>

      </div>
    );
  };


const Playlist_select = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch('https://api.spotify.com/v1/me/playlists?limit=50&offset=0', {
headers: {
  'Authorization': `Bearer ${accessToken}`
}
})
      .then(response => response.json())
      .then(json => setData(json))
      .catch(error => console.error(error));
  }, []);

  // data ? console.log(data.items[4]) : console.log("pending")




  return (
    <div id="playlist_select">
      {/* {data ? <pre>{JSON.stringify(data, null, 2)}</pre> : 'Loading...'} */}
