    try {
        const response = await fetch(`https://api.spotify.com/v1/me/player/next`, {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${accessToken}`,
          },
        });

        if (!response.ok) {
          throw new Error('Failed to skip');
        }

        console.log('skipped');
        Get_current_song_data()

      } catch (error) {
