  return (
        <div className="Head">
            <section id="profile">
            <h2>Logged in as {profile_data.displayName}</h2> 
            <img src={profile_data.profileImage}></img>
            <ul>
                <li>User ID: <span id="id"></span></li>
                <li>Email: {profile_data.email}</li>
            </ul>
            </section>

        </div>
  );
}


