const Get_available_devices = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch('https://api.spotify.com/v1/me/player/devices', {
headers: {
  'Authorization': `Bearer ${accessToken}`
}
})
      .then(response => response.json())
      .then(json => setData(json))
      .catch(error => console.error(error));
  }, []);

  console.log(data)
}


const GetCurrentSongData = async () => {
    try {
        const response = await fetch(`https://api.spotify.com/v1/me/player/currently-playing`, {
            headers: {
                'Authorization': `Bearer ${accessToken}`
            }
        });
        const data = await response.json();
        // console.log(data); // This will log the data to the console

        // Return the current song data
        return {
            "duration": data?.item?.duration_ms,
            "artists": data?.item?.artists,
            "name": data?.item?.name,
        };
    } catch (error) {
        console.error('Error:', error);
        return null; // Return null or handle the error as needed
    }
};



const playPlaylist = async (playlistId) => {
  // Get_available_devices()
  try {
    const response = await fetch(`https://api.spotify.com/v1/me/player/play`, {
      method: 'PUT',
      headers: {
        'Authorization': `Bearer ${accessToken}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        context_uri: `spotify:playlist:${playlistId}`,
        // context_uri: `spotify:track:4iV5W9uYEdYUVa79Axb7Rh`,

      }),
    });

    if (!response.ok) {
      throw new Error('Failed to play playlist');
    }

    console.log('Playlist is now playing');
    // Get_current_song_data()

  } catch (error) {
    console.error('Error:', error);
  }
};

const handle_playlist_click = (playlist_id) => {

