        <p className='playlist_name'>{playlist_json.name}</p>

      </div>
    );
  };


const PlaylistSelect = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch('https://api.spotify.com/v1/me/playlists?limit=50&offset=0', {
headers: {
  'Authorization': `Bearer ${accessToken}`
}
})
